INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (7310, '这是我第一次很认真的看展，关键是他免费啊，虽说是免费的，但是他的展厅内容也并不含糊。每一幅画都有他的故事，尤其看到汶川系列的时候，眼睛都湿润了。喜欢看展的朋友，值得来！不管是展厅，还是外观，四周环境，都可以拍出很美的照片来！', '2021-10-14', '玲小丽', 5, '李自健美术馆');
