INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (7381, '带爹妈2019年的6月底湖南大学打卡，其实打完卡就去爬岳麓山了，自己做了妈后才更感觉一定要多点时间带爹妈多旅游多陪陪他们，虽然我平时一周就去几次娘家很勤快的哈哈哈，我很喜欢长沙的，多次来了，以后还要来的。', '2019-08-14', 'yoyolove7788', 4, '湖南大学');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (7382, '位于岳麓山风景区呢，是个风水宝地呢，可以一起参观参观哈。学校建筑挺复古的呦，听说很多是民国时期传承下来的呢。湖南大学最大的特点就是没有校园啦，虽然没有校门，但是它的门禁措施做得挺好的呢，校园也挺安全哒。', '2019-08-13', '小民2011', 5, '湖南大学');
