INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (7384, '【景色】还不错！这里是去岳麓山必经之路，走在长沙绿道上，看着一路的景色，说实话，挺不错的！人很多，拍照不容易啊！ 【趣味】景点很多，随处可拍，也有小吃，吃的估计都是外地人！现在学校已经放假了。但丝毫不觉得，实在是人太多！ 【性价比】很高！因为免费，随便去！来湖南，不看看湖南大学怎么行，还是很好哒！', '2019-07-20', '童小佟', 5, '湖南大学');
