INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (7388, '当年在长沙医学院上学时，因为学校在望城县离汽车西站都比较远。懒得去市区，辅导员一直建议我们去湘雅医学院看看。呵呵，最后想不到去大学城是陪一土家族哥们去分手的，他怕挨揍', '2019-05-13', '还不曾去过倒悬山！', 4, '湖南大学');
