INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (7443, '橘子洲出来坐公交前往黄兴路，由于当时修路，公交绕路行驶，导致坐过了站。不过阴差阳错的走到了杜甫江阁，之前查攻略大家都说不值得去，就在外面看了看。两个吃货边看地图边打听，终于找到了步行街。先到有名的长郡中学看看，顺便再在中学的小吃一条街吃点小吃（其实吃才是主要目的）。', '2016-07-27', 'wrvl2394', 5, '黄兴路步行街');
