INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (7734, '此处游览免费，凭身份证领取参观卷。我不记得是初中还是高中了，话说那时学过的《过秦论》你还能背几句？贾谊其人其事，百度即可。他的这份才情见识和人生所历，足够让人唏嘘了！原路返回，太平街南口马路对面BOBO国际24楼电梯口右侧左拐，培荣书屋在静悄悄的尽头处。此行的突发奇想的由头和主要目的地。', '2017-01-21', 'hanfeidzyj', 0, '贾谊故居');
