INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (7747, '现在维修提质改造，应该在网络上告诉或提醒游客，不要浪费时间和走冤枉路，导航路线不准，中间文礼路在修路，可以走西北环线，有三条路线', '2016-09-23', '300****604', 1, '道吾山');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (7748, '森林覆盖率很高，很凉爽的，漫步山林间小清新的空气袭来忘却烦恼', NULL, '0306YHM', 5, '道吾山');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (7749, '道吾山风景优美，纯天然自然风景，空气清新，自然、舒适，爬爬山，吸吸氧，融入到大自然之中，非常的开心和愉快，趣味性强，不错哦！', NULL, 'gz当地向导伊妹儿', 5, '道吾山');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (7750, '山岳地貌奇特林泉飞瀑遍布，充满原始粗犷野趣。', NULL, '华-Rainbow', 4, '道吾山');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (7751, '市区边上，没多少景色，还要门票。不推荐专程去游玩', NULL, '210****911', 3, '道吾山');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (7752, '道吾山地理环境优美，地处浏阳境内距长沙65公里，是革命的老苏区，也是世界闻名的花炮之乡。以前陪家里散心来过这里，印象最深的就是天湖迷人的精致，湖面平静如镜倒影天空，蓝天白云朵朵也放佛浸入水中，四周绿树环绕，不远处梯田层层连绵，爬山看水，这才是生活。', NULL, '福二', 3, '道吾山');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (7753, '道吾山是浏阳名山、空气非常的清新凉爽、森林葱茏、登上山顶看梯田、美如画境、那天池的水、是那么的晶莹剔透、似镶嵌的一颗宝珠。光彩四溢。', NULL, '足行天下', 4, '道吾山');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (7754, '大自然的味道山顶上有很多农家乐我们去的时候中午了在西冲农家乐吃的往中间店铺老板娘人特别好分量多美味~下次还来有家的味道。推荐菜#油炸鱼#夏天真的很适合歇凉', NULL, '_WB2****462', 5, '道吾山');
