INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (7965, '月亮岛，一个富有浪漫气息的江中小岛，它犹如湘江中的一弯修长的新月，小岛原名遥阜洲，因明代有许姓人家在此耕耘，而叫许家洲又形如弯月，近年来渐有了这个迷人的雅号。', '2015-07-26', '林下清风1989', 5, '月亮岛');
