INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (7991, '裕湘纱厂，一个真正没人去的地方。其实那里也只有虚名了，只是在原址基石上重建了一排民国时期的洋房而已，不过有单反的朋友还是可以去取取景的！最不好的就是交通超级不便！原本计划一个钟的，结果等公交等半天最后还是打的去，回来的时候还走了一公里才能打的，累哭。', '2015-04-09', 'FASTER3', 3, '裕湘纱厂');
