INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (7993, '河西银盆岭大桥下有一座裕湘纱厂 一度繁荣 毁于文夕大火 很多工人和职员活活烧死 曾经的规模如今只有半座大门 一幢办公楼和一幢厂房留存 于是问题来了 这么多年 住在对岸的居民常看到办公楼里亮着灯光 有脚步声来来回回 厂房里常有诡异声响 我有幸在它是原貌时和同学撬了封板进去探险 可惜的是五六年过去 现在裕湘早就修缮一新 成为了一处人文景观 知道那里的人不多 去的更少 虽然看不出原来的可怖样貌 但是风...', NULL, '小只旅行记', 5, '裕湘纱厂');
