INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8226, '最最差的一次漂流，在株洲开了2个多小时的车，漂流时间短，漂流又没有落差，没有一点漂流的感觉，感觉有点上当受骗的感觉，下次再也不会去了。', '2019-08-12', 'M22****5443', 1, '大围山峡谷漂流');
