INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8228, '李富春故居就紧靠在坡子街，步行街，王府井，太平街，悦方，旁边也有很多吃的店，很方便。交通特别便利，参观时间是周二到周日上午9:00-下午17:00（16:30后停止入馆）。去参观带有效证件到值班室登记就可以免费入馆参观。', '2015-04-27', '去哪儿用户', 5, '李富春纪念馆');
