INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8275, '冲着宁乡沩山的漂流来的，却被这里的历史文化所吸引，这里有一座千年古刹---密印寺。这次去朝圣一下这个千古名寺，真的很棒，这种神秘远古的历史信仰感，让人觉得很神圣，仿佛被净化。', '2018-09-26', '_WeCh****970624', 5, '沩山风景名胜区');
