INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8385, '沩山空气真好，景色很美，是个旅游度假的好地方，听说是个许愿灵验的地方哦，因为那里有万尊佛的万佛寺和一尊很高很大的千手观音，建议朋友们一定得去体会一下', NULL, '路过风雲', 5, '沩山风景名胜区');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8386, '宁乡为山密印景区管委会     我们3位老人80岁以上，2017年5月13日上午到密印景区游玩，中午在观音圣像地发现86岁的老爷爷不见了，两个老奶奶很急，保安知道后，帮找到了，1位保安上山牵老奶奶下山，还有保安送我们到乘车路上，我们很感动，景区管理工作很周到，保安素质很高，热情负责。当班的6位保安值得表扬肯定，非常感谢！                                                                             ...', NULL, '188****5186', 5, '沩山风景名胜区');
