INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8392, '非常原始的地方，农耕农作 有漂流，世界最大的千手观音，门票不是很贵，由手工擂制的擂茶和当地特产毛尖茶，是个适合周末出游散心的好地方。', NULL, '187****6507', 4, '沩山风景名胜区');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8393, '网上订票很划算。景色大同小异，和以前去过的一些洞差不多。住宿我们是去的镇上的家庭旅馆，很干净才70一晚，我们因为时间充足，吃饭是自己去市场买菜，自己做饭。下午去漂流，很刺激。', NULL, 'TONGCHENG_USER', 4, '千佛洞');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8394, '宝贝玩得比较开心，个人觉得一般般，开了两个多小时的车，到那里玩了也就个把小时，没太多看头，主要是离得太远了，贸然这样跑一趟感觉挺划不来的～，而且门票也不便宜，较贵', NULL, 'TONGCHENG_USER', 5, '千佛洞');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8395, '千佛洞位于长沙市宁乡县黄材镇，比较冷门的景点，距离市区有点距离，而且游客也不是很多。', NULL, 'LAUALLEN', 4, '千佛洞');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8396, '千佛洞还是挺有趣的，可以看一些从没见过的。宁乡千佛洞位于距宁乡县城62公里处的西部山区，境内山清水秀，奇峰峻岭，石龙洞、峡溪、猴公大山，少年水库四大景点共同勾勒出崔坪的秀美和壮丽。', NULL, 'tb93058566', 5, '千佛洞');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8397, '宁乡千佛洞，只能说还行。走了那么远，乡村的气息很浓。洞口附近的农家，饭菜不错。', NULL, '小凉同学', 4, '千佛洞');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8398, '不错的景点，方便的网站，马上订立马就可以取票。导游态度好，负责，赞一个', NULL, 'TONGCHENG_USER', 5, '千佛洞');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8399, '说是千佛洞，其实仅仅一丁点钟乳石，被超级牵强地冠上些名字。规划很差，整个半成品，就是入洞前坐船进入那几分钟还有点意思。人气超差，导游的普通话够你琢磨几下，呵呵。总之，如果之前从未看过溶洞、钟乳石的去下可以，要是去过凤凰奇粱洞等等的亲放弃吧！浪费人力物力！', NULL, 'TONGCHENG_USER', 3, '千佛洞');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8400, '千佛洞-形成于三亿五千万年前的地壳运动，典型的喀斯特地貌，俗称十三洞，因其溶结成佛千座，祭拜活动频繁，后更名千佛洞。', NULL, '旅行达人 v10', 5, '千佛洞');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8401, '就是山清水秀，再没有什么特别的地方i昂了，但是还是非常值得以来的，真心是不错的的呢。', NULL, '千渡', 4, '千佛洞');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8402, '个人感觉这个地方相当不错。洞谷幽深，迂回曲折，神秘莫测。洞洞相连，洞内有洞，大的洞可容纳数千人，狭窄处仅容纳一人通过。洞内的石钟乳、石笋、石柱千姿百态，阴河、瀑布暗地横生，奇景迭出。非常漂亮，大自然鬼斧神工，让人惊诧不已，值得一游', NULL, '暗夜逍遥', 5, '千佛洞');
