INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8415, '洞内天然怪形怪石不错，可惜保护不好，很多钟乳石被人盗了一截。本来玩的还可以出来被坑闹心，导游说每人免费照相，领免费相片竟然要几十块钱一张，不经游客同意把你相片洗出来还要钱，不给钱就不给你相片。打110告骗人侵犯个人肖像也没用，保护伞真硬。想对黑商家说：我这才是真实的优质好评。', '2019-02-24', 'rxsz3213', 0, '千佛洞');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8416, '溶洞还是值得一看，开发的没有完全到位，不过导游讲解和关心非常到位，时时刻刻都在提醒小心台阶注意碰头。', '2018-05-13', '去哪儿用户', 0, '千佛洞');
