INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8611, '很适合一家人带着小孩去休闲一下。虽然不大，但是人不多。上午下午都有定时放飞蝴蝶的时间，小朋友最喜欢的是免费捞鱼。金鱼可以带回家的。', NULL, 'TONGCHENG_USER', 4, '光明蝶谷飞鸟乐园');
