INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8613, '亲子游不错的地方，5D电影很给力，大自然呼吸机，周末好去处，自驾方便，老人和学生半价。', NULL, 'TONGCHENG_USER', 5, '光明蝶谷飞鸟乐园');
