INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8618, '空气清新,环境干净宁静,有放飞蝴蝶,登山寻宝,捞小蝌蚪捞小鱼活动可参与,我们还惊喜地发现了大蘑菇和鸟窝,孩子玩得很开心,期待下次来时儿童乐园和餐厅能建好.里面暂无餐厅,建议吃饱再进.总的来说,很愉快很惬意！', NULL, 'TONGCHENG_USER', 5, '光明蝶谷飞鸟乐园');
