INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8647, '本来星期六去的。然后因为天气原因又变成星期天去。可是客服服务态度超好。帮着我们改来改去的。玩得很开心哦。风景好。水质干净很美的哦！我妹妹不到1.2签了一个免责还是可以玩的。玩得很开心！望生意越来越好。', '2015-08-13', '君君是吃货u', 0, '大围山峡谷漂流');
