INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8650, '我们今天选择去炭河千古情，但是看某地图上的路线，需要4个小时，上百个站才能到达，所以，们选择租车前往，只需要2个小时即可，租车的价格也比较便宜，一百多一天租了一辆高尔夫嘉旅。炭河古城的门票是：160元，包括了演出票以及门票，亲子票是90元。来了炭河古城，才发现眼前的景色如此秀丽，随处可见飘舞的红灯笼，处处编钟古乐，走在市井街，如同走在了三千多年前的老街，这里处处书写着炭河的前世今生。...', '2019-04-25', '去哪儿用户', 0, '炭河古城');
