INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8655, '炭河里遗址而来，位于湖南省长沙市宁乡市，为中国首个周文化主题公园。周朝是华夏文明的源头及鼎盛时期，创造过辉煌灿烂的文化奇观。值得一游的景点。', '2019-04-02', NULL, 0, '炭河古城');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8656, '炭河里遗址而来，位于湖南省长沙市宁乡市，为中国首个周文化主题公园。周朝是华夏文明的源头及鼎盛时期，创造过辉煌灿烂的文化奇观。值得一游的景点。', '2019-04-02', 'Ivan', 5, '炭河古城');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8657, '今天和妹妹两个人一起报团游玩的，小雷导游活泼可爱风趣热情，讲解详细，团餐相当Ok的，有鸡有鸭，有荤有素，参观完炭河里青铜博物馆，对面就是宋城炭河古城，古香古色的建筑，里面工作人员也穿着古装，过来过去很有气氛，到了宋河炭河古城一定要看炭河千古情这场演出，舞美灯光音效都很棒，一种身临其境的感觉，来长沙一定要去哟。', '2019-03-21', 'rubs9191', 0, '炭河古城');
