INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (8861, '在长沙等火车的时候在周围闲逛，看到有一处公园就去看看，原来这就是晓园公园。公园不大，一会就转完了，园中建有中日“友好和平”青铜群体塑像。还建有“东苑”、“园中园”，湖边设有“云水居”茶楼，“聚远楼”服务部。在湖岸和小山上建有形状各异的“迎旭亭”、“双秀亭”、“半亭”等建筑小品园内还辟有儿童游乐区，设置有多座人体、动物群雕。其实我最喜欢的是公园里的小花，紫色的千日红，黄色的小菊花...', '2014-09-26', 'gaofengfg', 3, '晓园公园');
