INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (9002, '周末等节假日人超级多，长沙人享受生活，家庭游，有山有水，有汽车影院，有烧烤。有儿童娱乐设施。慢慢逛逛，享受慢时光。门口桥下有停车场。湖南大学，洋湖湿地公园。相距不远。', '2017-03-08', '水蜜桃味的醒目', 5, '南郊公园');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (9003, '南郊公园风景不错，景色优美，空气清新自然，是休闲娱乐的好地方，趣味性非常强，公园散步、聊天都是不错的选择性价比超高的，不错哦！', '2016-11-03', 'gz当地向导伊妹儿', 4, '南郊公园');
