INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (9005, '景色不错，山青水秀，小朋友的乐园，性价比超高。', '2016-03-01', 'gz当地向导伊妹儿', 4, '南郊公园');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (9006, '清早刚下过一场大雨，行走在路上，凉爽的清风阵阵迎面扑来，在上班途中路过南郊公园。南郊公园位于猴子石大桥桥东，背靠湘江。于是进去到里面呼吸一下新鲜的空气，在公园前的广场上看到一群老爷爷们打着太极，继续走经过一个鱼塘边上的一座亭子，往山上走，一路上，来这晨练的都是一些老爷爷老奶奶，要么就是叔叔阿姨级别的，很少看到像我这年纪的年轻哥哥。', '2015-07-26', '林下清风1989', 5, '南郊公园');
