INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (9009, '南郊公园竹林拍照还是很适合的，有泉水可以洗手，主要是周边适合野餐，现在的天气再好不过了，可以的话还可以去游乐设施玩耍，就是如果大型设施拉客的能少一点就好了', '2018-04-03', '桃子1014', 5, '南郊公园');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (9010, '南郊公园可以称得上是城市花园，在繁华的城市中留下一片宁静留给人们去忘记工作中的压力或烦心事，让你感觉到享受生活是多么的美好！', '2015-12-19', NULL, 5, '南郊公园');
