INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (9094, '道观在岳麓山上，四周郁郁葱葱，秋天枫树鲜红鲜红格外漂亮。国内道观比寺庙数量少多了啊，这个道观建于明代，历史上多次被毁，规模比鼎盛时期小了许多', NULL, 'E44****23', 4, '云麓宫');
