INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (9319, '“杜甫江阁”，在江边，从酒店走过去并不远，大约也就十几二十分钟，6点出门就不能够登高望远了，本来我们也只打算做一个楼外看客，看看它的美，感受一下居民江边休闲的百态。如果想要登上杜甫江阁看看风景的话，建议可以把这个点安排在早上，不过赶得上登高，你也和夜景衔接不上呀，按照我们的时间就完全可以，走到杜甫江阁是六点多，天空还微微亮，风吹柳絮，还有人蹲坐在江边钓鱼，好休闲的感觉，大家沿着...', '2018-08-19', NULL, 0, '杜甫江阁');
