INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (9321, '杜甫江阁位于长沙湘江边上，门票11元，今天是周日，但游人太少，我不知道这是一个景点，开始从它的后边经过，回来时从它的前边经过，这都是为了避太阳的缘故，长沙太阳太毒，气温达到36——37度。经过前门才看到门票信息！可是，卖票的把窗户关了，这时来了一个本地妹带几个外国妹想游，她们敲门，我也跟着买票，大约10分钟就游完，没多少东西可看，就是爬一下楼，洲览一下里面的杜甫诗词和一些名人事迹！', '2018-07-29', '爱好和平', 4, '杜甫江阁');
