INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (9447, '免费的景点，面积虽然不大，但是是5A级的景区，主要因为其在中国历史中非常重要的地位。', '2015-10-29', 'WillSum', 5, '新民学会旧址');
