INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (9483, '步行街在这一片真的超多，比如坡子街、太平街、都正街，黄兴路步行街也是其中一个这条街的红路灯真的是人潮涌动啊从黄兴路步行街向南走，就是五一广场了这一条路的两边都是各种商场本身的商业街里面人流量也是真的很多好吃的好玩的店铺超级多虽然说有一些店铺是重复的，但是因为人多，每一家都在排队•坐标：芙蓉区黄兴南路司门口•交通：地铁黄兴广场站，两个口都在商业街里面•开放时间：全天开...', '2019-11-20', '筱风姿', 4, '黄兴路步行街');
