INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (9853, '樱花季不用去日本，也不用去其他城市，家门口就可以赏樱花啦！长沙望城区高塘岭路与航空西路附近的玉湖公园不用预约就能免费游览。自驾直接导航“玉湖公园”即可到。从长沙市区自驾前往需要1小时左右。公园停车场非常小，观花季私家车非常多，停车不是很方便。玉湖公园离月亮岛风景区和砂之船奥特莱斯车程大概14公里。自驾从长沙市区出发前往沿着湘江边开过月亮岛后，继续向北开到望城区高塘岭路附近，就能...', '2021-03-20', 'a锅_alison', 4, '玉湖公园');
