INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (9877, '地理位置很偏僻。去的时候，雪还没化，结果广场上还开喷泉，好冷。先要去景观塔一楼凭身份证自助取票。然后出塔，走外面台阶上博物馆参观。切记观光塔对面一楼的规划展览馆和市博物馆不互通。必须从外面上台阶才能进市博物馆。最近在二楼在办特展--殷商王后妇好的展览。有兴趣的可以先看一下。一楼是长沙市历史文物民俗展。早期历史跟省博湖南省叙述结构差不多，就是市博物馆复制件多些。比较有特色的是展览了...', NULL, 'e80****75', 5, '长沙市博物馆');
