INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (10162, '再探文和友臭豆腐博物馆，去年带着爹妈和小公主，今年和小公主两个人来长沙自由行。     玩了一圈大概半小时哈哈，那个闻味道的机器简直了，臭是臭的来吃不消哇。     买了大众上面那个原价90+的大礼包，团购77大洋，再买了份臭豆腐杨梅汁套餐，团购真是可以省不少钱。     套餐里面有个口味没有了，男店员经过我同意换成同价位的蒜香，纸袋可惜要收费，男店员看出来我很抠门的，说塑料袋不要钱，好啊那就塑料...', NULL, 'yoyolove7788', 5, '文和友臭豆腐博物馆');
