INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (10371, '美孚洋行旧址位于长沙市人流密集的太平老街，周二至周日9:30-17:00免费对外开放（周一闭馆），之前需要刷身份证领票进馆，现在已经可以登记直接进入了。 里面有两层楼，复原了民国时期的场景。 一楼有模拟真人的湘绣娘正在刺绣，也有瓷器、剪纸和传统手工艺品。 二楼则有长沙特色佳肴食材制作展示，以及皮影戏和展厅。 整个美孚洋行环境风格复古，是长沙具有历史穿越感的小众景点。', '2019-08-21', '蓝海清风1号', 5, '美孚洋行旧址');
