INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (10425, '伍氏猪脚非常不错的店，爬完山啃几坨猪脚，舒服了，环境好，景色不错，风景优美，趣味性强，值得推荐给大家，性价比超高的，有特色。', '2019-01-11', 'gz当地向导伊妹儿', 4, '伍氏猪脚植物园店');
