INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (10571, '步行街分为内街和外街，内街又有三层。商铺众多，品牌齐全。除各种常规服装店、饰品店以外，这里还汇集了全国各地的小吃美食，旁边的坡子街更是长沙特色小吃的齐聚之处，有长沙人民耳熟能详的火宫殿、双燕楼、红梅冷饮等，数不胜数。', NULL, 'IvanFC', 5, '黄兴广场');
