INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (10576, '取票很方便，而且比现场买票便宜了三十五块钱。我是清明节去的，人特别多，好不热闹。最激动的是车行区看到了猛兽在外面行走。推荐大家去长沙生态动物园。', '2017-04-06', 'qunar用户', 0, '长沙生态动物园');
