INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (10582, '天气热得不行，步行又累得很，不过取票挺快的，里面的水最少五元一瓶，光买水就买了好几十块，越喝越热，越热越喝，做乘车区司机开的太快，走马观花，老虎狮子热得不行，呼呼大睡。电动车三次打卡机会，只用了两次。做125路返回，累得不行了！不过很开心！', '2015-07-15', 'xhri7711', 0, '长沙生态动物园');
INSERT INTO `warehouse_evaluate` (`eid`, `content`, `send_time`, `user_name`, `score`, `scenery_name`) VALUES (10583, '去哪儿旅行用起来很方便，取票得便捷，服务态度好。动物园挺大的，环境不错，动物有点懒洋洋，但孩子们玩得很高兴，尤其喜欢大象表演、鸟艺表演、森林剧场等各种演出。想要游完全园，看完所有表演，一定要早点去，他们下午四点多就关馆了。', '2017-04-30', '去哪儿用户', 0, '长沙生态动物园');
